import React from "react";

class AddFishFrom extends React.Component {
  nameRef = React.createRef();
  priceRef = React.createRef();
  statusRef = React.createRef();
  descRef = React.createRef();
  imageRef = React.createRef();

  createFish = e => {
    e.preventDefault();
    const fish = {
      name: this.nameRef.current.value,
      price: this.priceRefnameRef.current.value,
      status: this.statusRefnameRef.current.value,
      desc: this.descRefnameRef.current.value,
      image: this.imageRefnameRef.current.value
    };
    this.props.addFish(fish);
    event.currentTarget.reset();
  };

  render() {
    return (
      <form className="fish-edit" onSubmit={this.createFish}>
        <input name="name" type="text" placeholder="name" ref={this.nameRef} />
        <input name="price" type="text" placeholder="price" ref={this.priceRef} />
        <select name="status" ref={this.statusRef}>
          <option value="available">Fresh</option>
          <option value="unavailable">Sold Out</option>
        </select>
        <textarea name="desc" ref={this.descRef} placeholder="description" />
        <input name="image" type="text" placeholder="image" ref={this.imageRef} />
        <button type="submit">+ Add Fish</button>
      </form>
    );
  }
}

export default AddFishFrom;
